    private void clearBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_clearBtnMouseClicked
        menuName.setText("");
        priceTxt.setText("");
    }//GEN-LAST:event_clearBtnMouseClicked

    private void saveBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_saveBtnMouseClicked
        Menu menu = new Menu();
        if (menuName.getText().isEmpty() || priceTxt.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Bütün Xanaları Doldurun!!!");
        } else {
            try {
                menu.setMenuName(menuName.getText());
                menu.setPrice(Float.valueOf(priceTxt.getText()));

                boolean isAdded = dao.add(menu);
                if (isAdded) {
                    JOptionPane.showMessageDialog(null, "Yemək Menyuya Əlavə Olundu...");
                    this.setVisible(false);
                } else {
                    JOptionPane.showMessageDialog(null, "Yemək Menyuya Əlavə Olunmadı!!!");
                }
            } catch (Exception ex) {
                Logger.getLogger(AddMenu.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_saveBtnMouseClicked

   

