    private void saveBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_saveBtnMouseClicked
        Product product = new Product();
        if (productNameTxt.getText().isEmpty() || numTxt.getText().isEmpty()
                || amountTxt.getText().isEmpty() || priceTxt.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Bütün Xanaları Doldurun");
        } else {

            try {
                product.setProductName(productNameTxt.getText());
                product.setNum(Integer.valueOf(numTxt.getText()));
                product.setAmount(Float.valueOf(amountTxt.getText()));
                product.setPrice(Float.valueOf(priceTxt.getText()));

                boolean isAdded = dao.add(product);
                if (isAdded) {
                    JOptionPane.showMessageDialog(null, "Produkt Əlavə Olundu...");
                    this.setVisible(false);
                } else {
                    JOptionPane.showMessageDialog(null, "Produkt Əlavə Olunmadı!!!");
                }
            } catch (Exception ex) {
                Logger.getLogger(AddProduct.class.getName()).log(Level.SEVERE, null, ex);
            }

        }
    }//GEN-LAST:event_saveBtnMouseClicked

    private void clearBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_clearBtnMouseClicked
        productNameTxt.setText("");
        numTxt.setText("");
        amountTxt.setText("");
        priceTxt.setText("");
    }//GEN-LAST:event_clearBtnMouseClicked

